		// TODO Auto-generated method stub
		short a=25;
		int b=123456894;
		long c=1234567891078l;
		byte d=124;
		float e=1.7894561f;
		double f=1.12345678945612;
		char g= 'S';
		boolean h=true;
		System.out.println("the short value is" +a);
